int bl = 5e5;
void exgcd(ll a, ll b, ll &d, ll &x, ll &y) {
    if (!b) {
        d = 1; x = 1; y = 0;
    }
    else {
        exgcd(b, a % b, d, y, x);
        y -= (a / b) * x;
    }
}
// a * x + p * y = 1
ll Inv(ll a, ll p = mod) {
    ll d, x, y;
    exgcd(a, p, d, x, y);
    return (x % p + p) % p;
}
ll calc(ll n) {
    int c = n / bl;
    int l = c * bl + 1;
    ll ans = f[c];
    rep(i, l, n) {
        ans += 1ll * (i - 1) * Inv(1ll * (i + 1) * (i + 2) % mod) % mod;
        if (ans >= mod) ans -= mod;
    }
    return ans;
}
int main() {
    int _; ll n, m;
    auto f = [&](ll n, ll m) -> ll {
        ll ans = (calc(n) - calc(m) + mod) % mod;
        ans = ans * (n + 1) % mod * (n + 2) % mod;
        return ans;
    };
    cin >> _;
    while (_ --) {
        cin >> n >> m;
        ll ans = (f(n, m) + mod - f(n - 1, m)) % mod;
        cout << ans << endl;
    }
    cout << clock() << endl;
}